function startEmulator(romData) {
    // Logique de démarrage de l'émulateur avec les données de la ROM
    if (typeof Emulator !== 'undefined') {
        Emulator.init(romData, document.getElementById('emulator-screen'));
    } else {
        console.error("La bibliothèque de l'émulateur n'est pas chargée.");
    }
}

function stopEmulator() {
    if (typeof Emulator !== 'undefined') {
        Emulator.stop();
    } else {
        console.error("La bibliothèque de l'émulateur n'est pas chargée.");
    }
}
